import React from "react";
import { css } from "styled-components"

export const CardContainer = ({children}) => {
    const container = {
        marginBottom: "2rem",
    }

    return (
        <div style={container}>
            {children}
        </div>
    );
}

export const StyledCard = ({children, ...props}) => {
    const card = {
        background: "rgb(163, 163, 163)", 
        position: "relative",
        display: "flex",
        justifyContent: "space-evenly",
        flexDirection: "column",
        alignItems: "center",
        width: "15rem",
        padding: "0.5rem",
        border: "none",
        "&:hover": {
            border: "0.5px solid rgb(206, 217, 142)",
        },
        boxShadow: "0 2px 4px rgba(0, 0, 0, 0.2)",
    }

    return (
        <div className="card" style={card} {...props}>
            {children}
        </div>
    );
}

export const CardImg = (props) => {
    const cardPic = {
        width: "100%",
        height: "auto",
        alignItems: "center",
        cursor: "pointer",
    }

    return (
        <img {...props} className="cardPic" style={cardPic} alt=""/>
    );
}

export const CardName = ({value}) => {
    const name = {
        margin: 0,
        padding: "1rem 1rem 1rem 1rem",
        fontSize: "18px",
        fontWeight: "bold",
    }

    return (
        <h3 style={name}>{value}</h3>
    );
}

export const CardUsername = ({value}) => {
    const user = {
        margin: 0,
        fontSize: "14px",
    }

    return (
        <p style={user}>{value}</p>
    );
}

export const CardFavoriteButton = ({children, ...props}) => {
    const favButton = {
        position: "absolute",
        border: "none",
        background: "none",
        cursor: "pointer",
        top: "90%",
        left: "85%"
    }

    return (
        <button {...props} className="favButton" style={favButton}>
            {children}
        </button>
    );
}

export const CardFavoriteButtonIcon = ({isFavorite}) => (
    <span role="img">{isFavorite ? "⭐" : "❤️"}</span>
)

export const link = `
display: flex;
alignItems: center;
justify-content: space-between;
padding: 1rem 2rem 1rem 2rem;
margin-bottom: 2rem;
width: 100vw;
background-color: var(--color-nav-fondo);
border-bottom: 1px solid var(--color-secundario);
transition: var(color-nav-dark) 0.5s;

${({$isDarkMode}) => $isDarkMode === true && css`
  background-color: var(--color-nav-dark);
  color: var(--color-texto-dark);
`}
`
